(ns playground2.core-test
    (:require [clojure.test :refer :all]
              [playground2.scrambler :refer :all]))
  

(deftest simple-scrable-valid
    (testing "codewarssdhsdhg has to be true for codewars"
        (is (= true (scramble? "codewarssdhsdhg" "codewars")))))

(deftest simple-scrable-invalid
    (testing "codewarssdhsdhg has to be false for codewarszx"
        (is (= false (scramble? "codewarssdhsdhg" "codewarszx")))))

;; samples from challenge
(deftest sample-scrable-valid-world
    (testing "”world” in rekqodlw”"
        (is (= true (scramble? "rekqodlw" "world")))))

(deftest sample-scrable-valid-codewars
    (testing "sample valid codewars in “cedewaraaossoqqyt"
        (is (= true (scramble? "cedewaraaossoqqyt" "codewars")))))

(deftest sample-scrable-invalid
    (testing "sample invalid steaks in katas"
       (is (= false (scramble? "katas"  "steak")))))

;;stressive
(deftest sample-scrable-invalid-longer-text
    (testing "sample invalid longer text"
       (is (= false (scramble? ""  
                                "steakzq")))))

(deftest sample-scrable-valid-longer-text
    (testing "sample valid with longer text"
        (is (= true (scramble? 
            "" 
            "")))))